const getCombination = (grid: string[][], sequences: string[], startingX: number = 0, startingY: number = 2): string => {
  let x = startingX;
  let y = startingY;
  const MAX_X = grid[0].length - 1;
  const MAX_Y = grid.length - 1;
  const code = []

  for (let i = 0; i < sequences.length; i++) {
    for (let j = 0; j < sequences[i].length; j++) {
      switch (sequences[i].charAt(j)) {
        case 'U':
          if (y > 0 && grid[y - 1][x] !== null) y--;
          break;
        case 'D':
          if (y < MAX_Y && grid[y + 1][x] !== null) y++;
          break;
        case 'L':
          if (x > 0 && grid[y][x - 1] !== null) x--;
          break;
        case 'R':
          if (x < MAX_X && grid[y][x + 1] !== null) x++;
          break;
      }
    }
    code.push(grid[y][x]);
  }
  return code.join('');
};

const input = `



`
const sequences = input.split('\n');

const keypadForA: string[][] = [
  ['1', '2', '3'],
  ['4', '5', '6'],
  ['7', '8', '9'],
];

const keypadForB: string[][] = [
  [null, null, '1', null, null],
  [null, '2', '3', '4', null],
  ['5', '6', '7', '8', '9'],
  [null, 'A', 'B', 'C', null],
  [null, null, 'D', null, null],
];

const comboA = getCombination(keypadForA, sequences);
const comboB = getCombination(keypadForB, sequences);
console.log(`Combo A: ${comboA}`);
console.log(`Combo B: ${comboB}`);